+ *
+ * @param msg The text of the message to parse.
+ */
+function extractPlaceholdersFromMsg(msg: string): Set<string> {
+  const regex = /{\$(.*?)}/g;
+  return extract(msg, regex, ([, placeholders]) => placeholders);
