+function extract<T>(
+    from: string, regex: RegExp, transformFn: (match: string[], state: Set<T>) => T): Set<T> {
+  const result = new Set<T>();
+  let item;
+  while ((item = regex.exec(from)) !== null) {
+    result.add(transformFn(item, result));
+  }
+  return result;
