 /* LAYOUT */

 .docs-content {
   position: relative;
+
+  /* API CLASS LIST */
+  .api-list {
+    list-style: none;
+    margin: 0 0 32px -8px;
+    padding: 0;
+    overflow: hidden;
+
+    @media screen and (max-width: 600px) {
+      margin: 0 0 0 -8px;
+    }
+
+    li {
+      @include font-size(14);
+      margin: 8px 0;
+      @include line-height(14);
+      padding: 0;
+      float: left;
+      width: 33%;
+      overflow: hidden;
+      min-width: 220px;
+      text-overflow: ellipsis;
+      white-space: nowrap;
+
+      .symbol {
+        margin-right: 8px;
+      }
+
+      a {
+        color: $blue-grey-600;
+        display: inline-block;
+        @include line-height(16);
+        padding: 0 16px 0;
+        text-decoration: none;
+        transition: all .3s;
+        overflow: hidden;
+        text-overflow: ellipsis;
+
+        &:hover {
+          background: $blue-grey-50;
+          color: $blue-500;
+        }
+
+        &.deprecated-api-item {
+          text-decoration: line-through;
+        }
+      }
+    }
+  }
 }

