+function getRecommendedOs({
+  arch,
+  ext
+}: {
+  arch: string;
+  ext: string;
+}): string {
+  switch (arch) {
+    case 'x64':
+      switch (ext) {
+        case '.dmg':
+          return 'x64 MacOS';
+        case '.AppImage':
+        case '.app.tar.gz':
+          return 'x64 Linux';
+        default:
+          return 'x64 Windows';
+      }
+    case 'aarch64':
+      switch (ext) {
+        case '.dmg':
+          return 'ARM MacOS';
+        case '.app.tar.gz':
+          return 'ARM Linux';
+        default:
+          return 'ARM Windows';
+      }
+    case 'amd64':
+      return 'x64 Linux';
+    default:
+      return '';
+  }
+}
