+      }
+    },
+    {
+      "character": "David",
+      "startTime": 72.28,
+      "finishTime": 78.72,
+      "dialogue": {
+        "text": "Perfect, Maria. Let's make sure everything is in place so we can meet our project milestones without any delays.",
