+```js
+const __oldRandom = Math.random;
+const __createRandom = () => {
+  let seed = 42;
+  const a = 1664525;
+  const c = 1013904223;
+  const mod = 2**32;
+  return () => {
+    seed = (a * seed + c) % mod;
+    return seed / mod;
+  };
+};
+
+const __setRandom = () => {
+  Math.random = __createRandom();
+};
+
+const __restoreRandom = () => {
+  Math.random = __oldRandom;
+};
+```
