+  "commands": [
+    {
+      "character": "James",
+      "position": { "x": 25, "y": 0, "z": 1 },
+      "startTime": 0
+    },
+    {
+      "character": "Sarah",
+      "position": { "x": 70, "y": 0, "z": 1 },
+      "startTime": 0.5
+    },
+    {
+      "character": "James",
+      "startTime": 1,
+      "finishTime": 3.2,
+      "dialogue": {
+        "text": "Sarah, have you heard about quantum computing?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "James",
+      "startTime": 3.6,
+      "finishTime": 5.4,
+      "dialogue": {
+        "text": "I just heard about it for the first time.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "James",
+      "startTime": 5.7,
+      "finishTime": 6.9,
+      "dialogue": {
+        "text": "Are you familiar with it?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 7.5,
+      "finishTime": 10.2,
+      "dialogue": {
+        "text": "I know a little. Quantum computing uses special computers",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 10.2,
+      "finishTime": 12.4,
+      "dialogue": {
+        "text": "to perform calculations extremely quickly.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "James",
+      "startTime": 12.9,
+      "finishTime": 16.1,
+      "dialogue": {
+        "text": "That's cool. But what can it do besides calculating things?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 16.6,
+      "finishTime": 18.2,
+      "dialogue": {
+        "text": "Well, it's not just for one thing.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 18.4,
+      "finishTime": 20.9,
+      "dialogue": {
+        "text": "Quantum computers can also help scientists with research.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 21.3,
+      "finishTime": 24,
+      "dialogue": {
+        "text": "They're useful for studying things like molecules and drugs.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 24.5,
+      "finishTime": 26,
+      "dialogue": {
+        "text": "They can help people invent new medicines.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 26.9,
+      "finishTime": 28.4,
+      "dialogue": {
+        "text": "Some people also use Quantum Computing",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 28.4,
+      "finishTime": 30.2,
+      "dialogue": {
+        "text": "for protecting information with encryption.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "James",
+      "startTime": 30.2,
+      "finishTime": 31.8,
+      "dialogue": {
+        "text": "Wow, that's amazing!",
+        "align": "left"
+      }
+    },
+    {
+      "character": "James",
+      "startTime": 32.1,
+      "finishTime": 34.7,
+      "dialogue": {
+        "text": "Is it hard to understand how Quantum Computers work?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 35.5,
+      "finishTime": 38.2,
+      "dialogue": {
+        "text": "They may seem complicated, but there are lots of simple videos",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 38.2,
+      "finishTime": 40.1,
+      "dialogue": {
+        "text": "that can help you understand the basic ideas.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "position": { "x": 125, "y": 0, "z": 1 },
+      "startTime": 40.6
+    },
+    {
+      "character": "James",
+      "position": { "x": -25, "y": 0, "z": 1 },
+      "startTime": 41.1
+    }
+  ]
+}
