+
+const stepMeta = {
+  ...baseMeta,
+  usesMultifileEditor: true,
+  hasEditableBoundaries: true
+};
+
+const quizMeta = {
+  ...baseMeta,
+  blockType: 'quiz',
+  blockLayout: 'link'
+};
+
+const languageMeta = {
+  ...baseMeta,
+  blockLayout: 'dialogue-grid'
+};
+
+export const getBaseMeta = (projectType: 'Step' | 'Quiz' | 'Language') => {
+  switch (projectType) {
+    case 'Step':
+      return stepMeta;
+    case 'Quiz':
+      return quizMeta;
+    case 'Language':
+      return languageMeta;
+    default:
+      return stepMeta;
+  }
+};

