+const retryingTest = (test, message, tries = 20) => {
+  if (tries < 1) return Promise.reject(message);
+  if (test()) return Promise.resolve();
+
+  return new Promise((resolve, reject) => {
+    setTimeout(() => {
+      retryingTest(test, message, tries - 1)
+        .then(resolve)
