+  poll.clear();
+  addOption('Turkey');
+  addOption('Morocco');
+  addOption('Spain');
+
+  vote('Turkey', 'traveler1');
+  vote('Turkey', 'traveler2');
+  vote('Morocco', 'traveler3');
+
+  assert.equal(
+    displayResults(),
+    'Poll Results:\nTurkey: 2 votes\nMorocco: 1 votes\nSpain: 0 votes'
+  );
+} finally {
+  poll.clear();
+  pollCopy.forEach((val, key) => {
