+import React, { useMemo } from 'react';
+import { useTranslation } from 'react-i18next';
+import { Spacer } from '@freecodecamp/ui';
+
+import {
+  certificationCollectionSuperBlocks,
+  chapterBasedSuperBlocks,
+  SuperBlocks
+} from '../../../../../shared-dist/config/curriculum';
