+      );
+  };
+
+  if (error) {
+    return <div>Error: {error.message}</div>;
+  }
+  if (loading) {
+    return <div>Loading...</div>;
+  }
+  return (
+    <div>
+      <h1>{chapterName}</h1>
+      <ul>
+        {items.map(chapter => (
+          <li key={chapter.name}>
+            <Link
+              to={`/${params.superblock}/chapters/${params.chapter}/${chapter.path}`}
