+      <hr />
+      <h2>Create New Project</h2>
+      <p>
+        Want to create a new project? Open your terminal and run{' '}
+        <code>pnpm run create-new-project</code>
+      </p>
+    </div>
+  );
+};
+
+export default ChapterLanding;

