+    },
+    {
+      "character": "Brian",
+      "startTime": 5.18,
+      "finishTime": 7.58,
+      "dialogue": {
+        "text": "I think we should focus on the mobile app version.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 8.22,
+      "finishTime": 9.92,
+      "dialogue": {
+        "text": "I'm not sure I agree with that, Brian.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 10.28,
+      "finishTime": 12.92,
+      "dialogue": {
+        "text": "The web app needs attention. It has more users.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 13.46,
+      "finishTime": 16.82,
+      "dialogue": {
+        "text": "Prioritizing the web app makes more sense given its larger user base.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Brian",
+      "startTime": 17.6,
+      "finishTime": 19.34,
+      "dialogue": {
+        "text": "Well, here's what I think we could do.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Brian",
+      "startTime": 19.48,
+      "finishTime": 22.4,
+      "dialogue": {
+        "text": "Why don't we allocate resources to both platforms equally?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Brian",
+      "startTime": 22.7,
+      "finishTime": 26.2,
+      "dialogue": {
+        "text": "We could give equal priority to both the web app and the mobile app.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 26.76,
+      "finishTime": 29.02,
+      "dialogue": {
+        "text": "I still think we should dedicate more time to the web app,",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 29.04,
+      "finishTime": 30.52,
+      "dialogue": {
+        "text": "but I see where you're coming from, Brian.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 31.16,
+      "finishTime": 33.3,
+      "dialogue": {
+        "text": "I think we can try giving equal priority for a while.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Brian",
+      "startTime": 33.56,
+      "finishTime": 35,
+      "dialogue": {
+        "text": "Good. Thanks for your support.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "position": { "x": 125, "y": 0, "z": 1 },
+      "startTime": 35.5
+    },
+    {
+      "character": "Brian",
+      "position": { "x": -25, "y": 0, "z": 1 },
+      "startTime": 36
+    }
