+        logger.warn(error);
       }
     }
+
+    const message =
+      reply.statusCode === 500 || isCSRFError
+        ? 'flash.generic-error'
+        : error.message;
+    if (accepts === 'json') {
+      void reply.send({
+        message,
+        type: 'danger'
+      });
+    } else {
+      void reply.status(302);
+      void reply.redirectWithMessage(returnTo, {
+        type: 'danger',
+        content: message
+      });
+    }
   });

   done();

