+.independent-lower-jaw {
+  position: absolute;
+  left: 0;
+  right: 0;
+  bottom: 0;
+  width: 100%;
+  z-index: 10;
+}
+
+.independent-lower-jaw .hint-container {
+  background-color: var(--background-quaternary);
+  display: flex;
+  flex-direction: row;
+  justify-content: space-between;
+  padding: 12px;
+  margin: 12px;
+}
+
+.independent-lower-jaw .btn-cta {
+  padding: 4px 10px;
+}
+
+.independent-lower-jaw .buttons-row-container {
+  display: flex;
+  justify-content: space-between;
+  flex-direction: row;
+  background-color: var(--background-secondary);
+  border: var(--background-quaternary) 1px solid;
+  padding: 12px;
+}
+
+.action-row-right {
+  display: flex;
+  flex-direction: row;
+  align-items: center;
+}
+
+.independent-lower-jaw .hint-container button {
+  height: 30px;
+  font-size: 1.5rem;
+  min-width: 30px;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  border: none;
+}
+
+.independent-lower-jaw .action-row-right button {
+  height: 40px;
+  width: 40px;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  border: none;
+  margin-left: 10px;
+}
+
+.independent-lower-jaw .tooltip {
+  position: relative;
+  display: inline-block;
+}
+
+/* Tooltip text */
+.independent-lower-jaw .tooltip .tooltiptext {
+  visibility: hidden;
+  opacity: 0;
+  background-color: var(--background-quaternary);
+  color: var(--foreground-primary);
+  border: 1px solid var(--foreground-secondary);
+  padding: 5px 10px;
+  font-size: 1rem;
+  text-align: center;
+  position: absolute;
+  top: -60px;
+  z-index: 1;
+}
+
+/* Show the tooltip text when you mouse over the tooltip container */
+.independent-lower-jaw .tooltip:hover .tooltiptext {
+  visibility: visible;
+  opacity: 1;
+  transition: opacity 0.5s ease 0.5s;
+}
+
+.tooltiptext::after {
+  content: '';
+  position: absolute;
+  display: block;
+  width: 0.5rem;
+  height: 0.5rem;
+  border-style: solid;
+  border-width: 0px 1px 1px 0px;
+  border-color: var(--color-border-primary);
+  transform: rotate(45deg);
+  bottom: -5px;
+  left: calc(50% - 0.25rem);
+  background-image: linear-gradient(
+    to top left,
+    var(--background-quaternary) 55%,
+    rgba(0, 0, 0, 0) 20%
+  );
+}
+
+.independent-lower-jaw .tooltip .tooltiptext.left-tooltip {
+  left: 0;
+}

