-  }
-
-  const maybeAttempts = await mapErr(
-    this.prisma.examEnvironmentExamAttempt.findMany({
-      where: {
-        userId: user.id
-      }
-    })
-  );
-
-  if (maybeAttempts.hasError) {
-    logger.error(maybeAttempts.error);
-    this.Sentry.captureException(maybeAttempts.error);
