+      {
+        "character": "Jake",
+        "position": { "x": -25, "y": 0, "z": 1 }
+      },
+      {
+        "character": "Sarah",
+        "position": { "x": 125, "y": 0, "z": 1 }
+      }
+    ],
+    "audio": {
+      "filename": "5.2-1.mp3",
+      "startTime": 1
+    },
+    "alwaysShowDialogue": true
+  },
+  "commands": [
+    {
+      "character": "Jake",
+      "position": { "x": 25, "y": 0, "z": 1 },
+      "startTime": 0
+    },
+    {
+      "character": "Sarah",
+      "position": { "x": 70, "y": 0, "z": 1 },
+      "startTime": 0.5
+    },
+    {
+      "character": "Jake",
+      "startTime": 1,
+      "finishTime": 4.78,
+      "dialogue": {
+        "text": "Sarah, can you explain data types to me? I'm a bit confused.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 5.4,
+      "finishTime": 7.68,
+      "dialogue": {
+        "text": "Sure. Data types are fundamental in programming.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 8.1,
+      "finishTime": 11.16,
+      "dialogue": {
+        "text": "You use them to tell the computer what kind of data you're working with.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Jake",
+      "startTime": 11.46,
+      "finishTime": 14.64,
+      "dialogue": {
+        "text": "Thanks, but I see many data types listed in the documentation.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Jake",
+      "startTime": 14.96,
+      "finishTime": 16.4,
+      "dialogue": {
+        "text": "How do I know which one to choose?",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 16.82,
+      "finishTime": 21.6,
+      "dialogue": {
+        "text": "Good question. You usually choose data types based on the kind of information you want to store.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 22.08,
+      "finishTime": 26.02,
+      "dialogue": {
+        "text": "For example, if you're working with text, you'd use this data type: string.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Sarah",
+      "startTime": 26.52,
+      "finishTime": 28.52,
+      "dialogue": {
+        "text": "If it's whole numbers, you'd choose int.",
+        "align": "right"
+      }
+    },
+    {
+      "character": "Jake",
+      "startTime": 29.18,
+      "finishTime": 34.64,
+      "dialogue": {
+        "text": "Ah, I see. So this means I should pick the data type that matches the information I want to work with.",
+        "align": "left"
+      }
+    },
+    {
+      "character": "Sarah",
+      "position": { "x": 125, "y": 0, "z": 1 },
+      "startTime": 35.14
+    },
+    {
+      "character": "Jake",
+      "position": { "x": -25, "y": 0, "z": 1 },
+      "startTime": 35.64
+    }
+  ]
+}
