+  outOfRangeError
 );
 common.expectsError(
   () => Buffer.alloc(16).writeDoubleLE(0, 9),
-  outOfBoundsError
+  outOfRangeError
 );

 // attempt to overflow buffers, similar to previous bug in array buffers
 common.expectsError(
   () => Buffer.allocUnsafe(8).writeFloatLE(0.0, 0xffffffff),
-  outOfBoundsError
+  outOfRangeError
 );
 common.expectsError(
   () => Buffer.allocUnsafe(8).writeFloatLE(0.0, 0xffffffff),
-  outOfBoundsError
+  outOfRangeError
 );

 // ensure negative values can't get past offset
 common.expectsError(
   () => Buffer.allocUnsafe(8).writeFloatLE(0.0, -1),
-  outOfBoundsError
+  outOfRangeError
 );
 common.expectsError(
   () => Buffer.allocUnsafe(8).writeFloatLE(0.0, -1),
-  outOfBoundsError
+  outOfRangeError
 );

