-  if (U_FAILURE(status)) {
-    return NULL;
-  }
-  CDFLocaleData* result = new CDFLocaleData;
-  if (result == NULL) {
-    status = U_MEMORY_ALLOCATION_ERROR;
-    return NULL;
-  }
-  result->Init(status);
-  if (U_FAILURE(status)) {
-    delete result;
-    return NULL;
-  }
-
-  load(inLocale, result, status);
-
-  if (U_FAILURE(status)) {
-    delete result;
-    return NULL;
