-static int32_t formatInt(
-        int32_t value, uint8_t *digits) {
-    int32_t idx = 0;
-    while (value > 0) {
-        digits[idx++] = (uint8_t) (value % 10);
-        value /= 10;
-    }
-    return idx;
-}
