-    }
-    {
-        UnicodeStringAppender appender(appendTo);
-        for (; i >= count; --i) {
-            appender.append(fLocalizedDigits[0]);
-        }
-        for (; i >= 0; --i) {
-            appender.append(fLocalizedDigits[digits[i]]);
-        }
-    }
-    handler.addAttribute(intField, begin, appendTo.length());
-    return appendTo;
