+function dec2char(n) {
+    // converts a decimal number to a Unicode character
+    // n: the dec codepoint value to be converted
+    if (n <= 0xffff) {
+        out = String.fromCharCode(n);
+    } else if (n <= 0x10ffff) {
+        n -= 0x10000;
+        out =
+            String.fromCharCode(0xd800 | (n >> 10)) +
+            String.fromCharCode(0xdc00 | (n & 0x3ff));
+    } else out = "dec2char error: Code point out of range: " + n;
+    return out;
+}
+
+function eucjpDecoder(stream) {
+    stream = stream.replace(/%/g, " ");
+    stream = stream.replace(/[\s]+/g, " ").trim();
+    var bytes = stream.split(" ");
+    for (var i = 0; i < bytes.length; i++) bytes[i] = parseInt(bytes[i], 16);
+    var out = "";
+
+    var lead, byte, offset, ptr, cp;
+    var jis0212flag = false;
+    var eucjpLead = 0x00;
+    var endofstream = 2000000;
+    var finished = false;
+
+    while (!finished) {
+        if (bytes.length == 0) byte = endofstream;
+        else byte = bytes.shift();
+
+        if (byte == endofstream && eucjpLead != 0x00) {
+            eucjpLead = 0x00;
+            out += "�";
+            continue;
+        }
+        if (byte == endofstream && eucjpLead == 0x00) {
+            finished = true;
+            continue;
+        }
+        if (eucjpLead == 0x8e && byte >= 0xa1 && byte <= 0xdf) {
+            eucjpLead = 0x00;
+            out += dec2char(0xff61 + byte - 0xa1);
+            continue;
+        }
+        if (eucjpLead == 0x8f && byte >= 0xa1 && byte <= 0xfe) {
+            jis0212flag = true;
+            eucjpLead = byte;
+            continue;
+        }
+        if (eucjpLead != 0x00) {
+            lead = eucjpLead;
+            eucjpLead = 0x00;
+            cp = null;
+
+            if (
+                lead >= 0xa1 &&
+                lead <= 0xfe &&
+                (byte >= 0xa1 && byte <= 0xfe)
+            ) {
+                ptr = (lead - 0xa1) * 94 + byte - 0xa1;
+                if (jis0212flag) cp = jis0212[ptr];
+                else cp = jis0208[ptr];
+            }
+            jis0212flag = false;
+            if (cp != null) {
+                out += dec2char(cp);
+                continue;
+            }
+            if (byte >= 0x00 && byte <= 0x7f) bytes.unshift(byte);
+            out += "�";
+            continue;
+        }
+        if (byte >= 0x00 && byte <= 0x7f) {
+            out += dec2char(byte);
+            continue;
+        }
+        if (byte == 0x8e || byte == 0x8f || (byte >= 0xa1 && byte <= 0xfe)) {
+            eucjpLead = byte;
+            continue;
+        }
+        out += "�";
+    }
+    return out;
+}

@@ -0,0 +1,128 @@
+var jis0208CPs = []; // index is unicode cp, value is pointer
+for (var p = 0; p < jis0208.length; p++) {
+    if (jis0208[p] != null && jis0208CPs[jis0208[p]] == null) {
+        jis0208CPs[jis0208[p]] = p;
+    }
+}
+
+function chars2cps(chars) {
+    // this is needed because of javascript's handling of supplementary characters
+    // char: a string of unicode characters
+    // returns an array of decimal code point values
+    var haut = 0;
+    var out = [];
+    for (var i = 0; i < chars.length; i++) {
+        var b = chars.charCodeAt(i);
+        if (b < 0 || b > 0xffff) {
+            alert(
+                "Error in chars2cps: byte out of range " + b.toString(16) + "!"
+            );
+        }
+        if (haut != 0) {
+            if (0xdc00 <= b && b <= 0xdfff) {
+                out.push(0x10000 + ((haut - 0xd800) << 10) + (b - 0xdc00));
+                haut = 0;
+                continue;
+            } else {
+                alert(
+                    "Error in chars2cps: surrogate out of range " +
+                        haut.toString(16) +
+                        "!"
+                );
+                haut = 0;
+            }
+        }
+        if (0xd800 <= b && b <= 0xdbff) {
+            haut = b;
+        } else {
+            out.push(b);
+        }
+    }
+    return out;
+}
+
+function eucjpEncoder(stream) {
+    var cps = chars2cps(stream);
+    var out = "";
+    var cp;
+    var finished = false;
+
+    while (!finished) {
+        if (cps.length == 0) {
+            finished = true;
+            continue;
+        } else cp = cps.shift();
+        if (cp >= 0x00 && cp <= 0x7f) {
+            // ASCII
+            out += " " + cp.toString(16).toUpperCase();
+            continue;
+        }
+        if (cp == 0xa5) {
+            out += " 5C";
+            continue;
+        }
+        if (cp == 0x203e) {
+            out += " 7E";
+            continue;
+        }
+        if (cp >= 0xff61 && cp <= 0xff9f) {
+            var temp = cp - 0xff61 + 0xa1;
+            out += " 8E " + temp.toString(16).toUpperCase();
+            continue;
+        }
+        if (cp == 0x2212) {
+            cp = 0xff0d;
+        }
+        var ptr = jis0208CPs[cp];
+        if (ptr == null) {
+            return null;
+            //          out += ' &#'+cp+';'
+            //          continue
+        }
+        var lead = Math.floor(ptr / 94) + 0xa1;
+        var trail = ptr % 94 + 0xa1;
+        out +=
+            " " +
+            lead.toString(16).toUpperCase() +
+            " " +
+            trail.toString(16).toUpperCase();
+    }
+    return out.trim();
+}
+
+function convertToHex(str) {
+    // converts a string of ASCII characters to hex byte codes
+    var out = "";
+    var result;
+    for (var c = 0; c < str.length; c++) {
+        result =
+            str
+                .charCodeAt(c)
+                .toString(16)
+                .toUpperCase() + " ";
+        out += result;
+    }
+    return out;
+}
+
+function normalizeStr(str) {
+    var out = "";
+    for (var c = 0; c < str.length; c++) {
+        if (str.charAt(c) == "%") {
+            out += String.fromCodePoint(
+                parseInt(str.charAt(c + 1) + str.charAt(c + 2), 16)
+            );
+            c += 2;
+        } else out += str.charAt(c);
+    }
+    var result = "";
+    for (var o = 0; o < out.length; o++) {
+        result +=
+            "%" +
+            out
+                .charCodeAt(o)
+                .toString(16)
+                .toUpperCase();
+    }
+    return result.replace(/%1B%28%42$/, "");
+}

