+for (const splitPoint of [2, 3, 4, 5]) {
+  promise_test(async () => {
+    const input = readableStreamFromArray(
+        [new Uint8Array(inputBytes.slice(0, splitPoint)),
+         new Uint8Array(inputBytes.slice(splitPoint))]);
