-function sysError(defaultMessage) {
-  return function(code,
-                  syscall,
-                  path,
-                  dest,
-                  message = defaultMessage) {
-    if (code !== undefined)
-      message += `: ${code}`;
-    if (syscall !== undefined) {
-      if (code === undefined)
-        message += ':';
-      message += ` [${syscall}]`;
-    }
-    if (path !== undefined) {
-      message += `: ${path}`;
-      if (dest !== undefined)
-        message += ` => ${dest}`;
-    }
-    return message;
-  };
+function sysError(code, syscall, path, dest,
+                  message = 'A system error occurred') {
+  if (code !== undefined)
+    message += `: ${code}`;
+  if (syscall !== undefined) {
+    if (code === undefined)
+      message += ':';
+    message += ` [${syscall}]`;
+  }
+  if (path !== undefined) {
+    message += `: ${path}`;
+    if (dest !== undefined)
+      message += ` => ${dest}`;
+  }
+  return message;
 }

