@@ -0,0 +1,49 @@
+'use strict';
+const common = require('../common');
+const { Writable } = require('stream');
+
+{
+  // Sync + Sync
+  const writable = new Writable({
+    write: common.mustCall((buf, enc, cb) => {
+      cb();
+      common.expectsError(cb, {
+        code: 'ERR_MULTIPLE_CALLBACK',
+        type: Error
+      });
+    })
+  });
+  writable.write('hi');
+}
+
+{
+  // Sync + Async
+  const writable = new Writable({
+    write: common.mustCall((buf, enc, cb) => {
+      cb();
+      process.nextTick(() => {
+        common.expectsError(cb, {
+          code: 'ERR_MULTIPLE_CALLBACK',
+          type: Error
+        });
+      });
+    })
+  });
+  writable.write('hi');
+}
+
+{
+  // Async + Async
+  const writable = new Writable({
+    write: common.mustCall((buf, enc, cb) => {
+      process.nextTick(cb);
+      process.nextTick(() => {
+        common.expectsError(cb, {
+          code: 'ERR_MULTIPLE_CALLBACK',
+          type: Error
+        });
+      });
+    })
+  });
+  writable.write('hi');
+}
