+  stream.on('error', (err) => assert.strictEqual(err.code, 'ECONNRESET'));
   stream.respondWithFile(process.execPath, {
     [HTTP2_HEADER_CONTENT_TYPE]: 'application/octet-stream'
   });
@@ -22,16 +24,9 @@ server.listen(0, common.mustCall(() => {
