+  PTR_INC(uint16_t, p16,
           (height - 1) * p16_row_bytes + (width - 1) * sizeof(*p16));
   int bump8 = width * sizeof(*p8) - p8_row_bytes;
   int bump16 = width * sizeof(*p16) - p16_row_bytes;
   for (; height-- != 0;
