+  --color-ansi-bright-yellow: var(--color-amber-900);
+
+  /* Background Light */
+  --color-background-100: #ffffff;
+  --color-background-200: #fafafa;
+
+  /* Syntax Light */
+  --color-syntax-comment: #545454;
+  --color-syntax-constant: #171717;
+  --color-syntax-function: #0054ad;
