+
+export function TurbopackInfo({
+  isOpen,
+  setIsOpen,
+  setPreviousOpen,
+  ...props
+}: {
+  isOpen: boolean
+  setIsOpen: (isOpen: boolean) => void
+  setPreviousOpen: (isOpen: boolean) => void
+  style?: React.CSSProperties
+  ref?: React.RefObject<HTMLElement | null>
