process.env.TURBOPACK = '1'

function stringifySourceInfo(source: SourceInfo): string {
  switch (source.type) {
    case SourceType.Runtime:
      return `runtime for chunk ${source.chunkPath}`
    case SourceType.Parent:
      return `parent module ${source.parentId}`
