    // eslint-disable-next-line no-eval -- Can't use vm.runInThisContext due to https://github.com/nodejs/node/issues/52102
    ;(0, eval)(
      '(function(module, exports, require, __dirname, __filename) {' +
        contents +
        '\n})' +
        '\n//# sourceURL=' +
        url.pathToFileURL(resolved)
