type ExternalImport = (id: ModuleId) => Promise<Exports | EsmNamespaceObject>

interface TurbopackNodeBuildContext extends TurbopackBaseContext<Module> {
  R: ResolvePathFromModule
  x: ExternalRequire
  y: ExternalImport
