const loadedChunks = new Set<ChunkPath>()

function loadChunkPath(chunkPath: ChunkPath, source?: SourceInfo): void {
  if (!isJs(chunkPath)) {
    // We only support loading JS chunks in Node.js.
    // This branch can be hit when trying to load a CSS chunk.
    return
